data.post_content:
.homepage-services-slider-wrap {
    height: 70rem!important;
}

.captcha-image {
    border: 1px solid;
    padding: 10px;
    display: inline-block;
    text-align: center;
    color: #fff;
	margin-bottom: 2rem;
}
.section-h1{
	padding: 60px 0;
  background-color: #0a0a0a38;
  margin-bottom: 8px;
}
.section-h1 h1{
    text-align: center;
    font-size: 28px;
    font-weight: 900;
    color: white;
    margin-bottom: 0;
}
.call-to-action {
    border: 4px blue double;
    padding: 20px;
    background: aliceblue;
}
p {
    font-weight: 400;
}
.footer-related-articles-wrap .related-articles .related-articles-item .foo-related-blog-details .foo-from-the-blog {
        color: #fff;

    }
@media (min-width: 992px) {
    .footer-related-articles-wrap .related-articles .related-articles-item .foo-relater-blog-image .foo-related-blog-overlay {

        background-color: #1a4a7252;

    }
}

@media (min-width: 992px) {
    .footer-related-articles-wrap .related-articles .related-articles-item .foo-related-blog-details .entry-meta span i {
        font-size: 18px;
        color: #fff;
        padding-right: 5px;
    }
}
@media (min-width: 992px) {
    .footer-related-articles-wrap .related-articles .related-articles-item .foo-related-blog-details .entry-excerpt p {
        margin: 0;
        font-size: 14px;
        color: #fff;
    }
}
@media (min-width: 992px) {
    .footer-related-articles-wrap .related-articles .related-articles-item .foo-related-blog-details h4 {
        font-size: 20px;
        font-weight: 600;
        color: #fff;
        margin-top: 10px;
        margin-bottom: 10px;
        padding-right: 20%;
    }
}
@media (min-width: 992px) {
    .footer-related-articles-wrap .related-articles .related-articles-item .foo-related-blog-details .entry-meta span {
        font-size: 13px;
        font-style: normal;
        font-weight: 400;
        text-transform: capitalize;
        margin-right: 30px;
        color: #fff;
    }
}

.city-list-wrapper {
  display: block;
  width: 100%;
}

.city-list-left,
.city-list-right {
  width: 49%;
  display: inline-block;
  vertical-align: top;
  box-sizing: border-box;
}
